INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (1, 'NT-1', 1, 'Prolonged drought & lack of water', 'Droughts become a problem for business when they reach the extreme of water rationing.', 1);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (2, 'NT-2', 1, 'Earthquakes', 'Earthquakes have a major impact on urbanized areas.', 2);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (3, 'NT-3', 1, 'Fires', 'Fires are always a concern due to the huge impact on business and life.', 3);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (4, 'NT-4', 1, 'Floods', 'Floods are the most common type of natural disaster and require understanding their frequency and flows and impacts in localities.', 4);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (5, 'NT-5', 1, 'Hurricanes and Tropical Storms', 'Interruptions in public services, in addition to the impossibility of mobility, must be considered in cases of hurricanes and tropical storms.', 5);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (6, 'NT-6', 1, 'Landslides and falling barriers', 'Landslides can have major impacts and require knowledge of local geology to assess their risk', 6);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (7, 'NT-7', 1, 'Spread of diseases (pandemic or endemic)', 'Due to the wide variety of possible diseases, their speed of spread and their impact, these factors must be taken into account when assessing risk.\r\n\r\n', 7);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (8, 'NT-8', 1, 'Severe weather event', 'Severe weather event is a broad category that includes everything from strong winds to storm surges.\r\n\r\n', 8);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (9, 'NT-9', 1, 'Space weather events', 'This category includes space events that can affect satellites. The most common are solar storms. Understanding the dependency on affectable devices is necessary for risk assessment.', 9);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (10, 'NT-10', 1, 'Storms & Lightning', 'Events caused by lightning can damage buildings and infrastructure. Your frequency of dependence on affectable devices must be considered at risk.', 10);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (11, 'NT-11', 1, 'Tornadoes', 'Tornadoes occur in several locations and can destroy a wide range of buildings.', 11);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (12, 'NT-12', 1, 'Tsunamis', 'All tsunamis are potentially destructive and can occur in any coastal area in the world.', 12);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (13, 'NT-13', 1, 'Volcanoes', 'Although volcanoes are fixed objects, a large eruption can have far-reaching effects, including ash rain and damage to crops.', 13);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (14, 'NT-14', 1, 'Winter storms and extreme cold', 'Winter storms can impact mobility in affected locations.', 14);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (15, 'MT-1', 2, 'Civil or political unrest', 'Civil or political unrest are events, specific or widespread, that may be unexpected or unpredictable. These events can occur at any time and in any place.\r\n\r\n', 15);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (16, 'MT-2', 2, 'Hacking & other crimes related to Cybersecurity', 'Unlike physical threats that generally trigger an immediate reaction (such as fleeing a fire), cybercrimes are generally difficult to identify while the incident is ongoing. Detection commonly occurs after the incident with the exception of denial of service attacks. The scope of cybersecurity risks is limitless and threats can have effects on individuals, organizations or services.', 16);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (17, 'MT-3', 2, 'Emergencies with toxic materials', 'Toxic materials emergencies are focused on accidental disasters that occur involving toxic materials. These incidents can range from chemical spills to soil or water contamination.\r\n', 17);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (18, 'MT-4', 2, 'Nuclear, biological or chemical weapons.', 'The use of such weapons by international terrorists must be considered possible. The use of a dirty bomb is considered more likely than the use of a nuclear weapon. A large impact area must be considered.', 18);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (19, 'MT-5', 2, 'Physical crime', 'Physical crimes include traditional crimes. These incidents can range from assault, theft, vandalism or destoning.\r\n\r\n', 19);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (20, 'MT-6', 2, 'Terrorism and armed attacks', 'Armed attacks, regardless of the attacker\'s motivation, can impact the business. Scenarios range from a rampage to armed robberies including hostage situations. Consideration should be given to the possibility of using bladed weapons and explosives in addition to firearms.\r\n\r\n', 20);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (21, 'MT-7', 2, 'Failure in public services', 'Failures in public services are focused on continued lack of electricity, water, gas, telephone or internet.', 21);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (22, 'TT-1', 4, 'Unmitigated vulnerabilities', 'The abuse of unmitigated vulnerabilities is one of the main causes of security failures in systems.\r\n\r\n', 22);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (23, 'MT-8', 2, 'Inadequate internal procedures', 'Inadequately created internal procedures can expose data and systems to failures.', 23);
INSERT INTO `threat_catalog` (`id`, `number`, `grouping`, `name`, `description`, `order`) VALUES (24, 'MT-9', 2, 'Social engineering', 'Attackers can use social engineering to deceive employees and obtain privileged information or improper access.', 24);
